/* eslint-disable @typescript-eslint/no-explicit-any */
import React, { Component } from 'react';
import { ReduxState, ReduxDispatch, OwnProps } from '../containers/types/appShellContainerWrapper';
import appShellContainerWrapper from '../containers/appShellContainerWrapper';

type ActualProps = ReduxState & ReduxDispatch & OwnProps;

class AppShell extends Component<ActualProps> {
    componentDidMount(): void {
        const { getUserDetails: fetchUser } = this.props;
        fetchUser();
    }

    render(): JSX.Element {
        const { children } = this.props;
        return (
            <>
                {children}
            </>
        );
    }
}

export default appShellContainerWrapper(AppShell);
