/* eslint-disable no-underscore-dangle */
/* eslint-disable @typescript-eslint/no-explicit-any */
/* eslint-disable @typescript-eslint/explicit-function-return-type */
/* eslint-disable react/jsx-props-no-spreading */
import React from 'react';
import { Provider } from 'react-redux';
import App from 'next/app';
import { createLogger } from 'redux-logger';
import withRedux, { AppProps } from 'next-redux-wrapper';
import initAppStore from '../js/redux/initAppStore';
import { isServer } from '../js/common/utils/server';
import AppShell from '../js/common/components/AppShell';
import config from '../../config/config';
import 'antd/dist/antd.min.css';


declare global {
    interface Window {
        __REDUX_DEVTOOLS_EXTENSION__: any;
    }
}

let devTools = (x) => x;
const extraMiddlewares = [];

if (config.IS_DEV_ENV) {
    const loggerMiddleware = createLogger();
    extraMiddlewares.push(loggerMiddleware);
    // eslint-disable-next-line no-underscore-dangle
    if (!isServer) {
        devTools = window.__REDUX_DEVTOOLS_EXTENSION__ ? window.__REDUX_DEVTOOLS_EXTENSION__() : (x) => x;
    }
}

const nextAppStore = (emptyInitalState) => initAppStore(emptyInitalState, { devTools, extraMiddlewares });

class MyApp extends App<AppProps> {
    static async getInitialProps({ Component, ctx }) {
        const pageProps = Component.getInitialProps ? await Component.getInitialProps(ctx) : {};

        return { pageProps };
    }

    render() {
        const { Component, pageProps, store } = this.props;
        return (
            <Provider store={store}>
                <AppShell>
                    <Component {...pageProps} />
                </AppShell>
            </Provider>
        );
    }
}

export default withRedux(nextAppStore, { debug: config.ENVIRONMENT === 'development' })(MyApp);
