/* eslint-disable @typescript-eslint/no-explicit-any */
/* eslint-disable @typescript-eslint/explicit-function-return-type */

/*
this file is always supposed to be a .js file converting it to .ts will break reducer injection!  -pranjalk 2019-12-27
*/

import {
    compose,
    createStore,
    applyMiddleware,
    combineReducers,
} from 'redux';
import thunk from 'redux-thunk';
import apiMiddleware from './middlewares/apiPromiseHandler';
import initReducers from './reducers/rootReducer';

const middlewares = [
    thunk,
    apiMiddleware,
];

function createReducer(asyncReducers) {
    return combineReducers({
        ...initReducers,
        ...asyncReducers,
    });
}

const appStore = (initialState, { devTools = (x) => x, extraMiddlewares = [] }) => {
    const bootupReducer = createReducer({});
    const store = createStore(
        bootupReducer,
        initialState, compose(
            applyMiddleware(
                ...middlewares,
                ...extraMiddlewares,
            ),
            devTools,
        ),
    );

    store.asyncReducers = {};
    store.injectReducer = (key, asyncReducer) => {
        store.asyncReducers[key] = asyncReducer;
        store.replaceReducer(createReducer(store.asyncReducers));
        // eslint-disable-next-line no-console
        console.log(`${key} reducer injected successfully`);
    };

    return store;
};

export default appStore;
