/* eslint-disable @typescript-eslint/no-explicit-any */
import { connect } from 'react-redux';
import { getUserDetails } from '../../redux/actions/userActions';
import { ReduxState, ReduxDispatch, OwnProps } from './types/appShellContainerWrapper';

const mapStateToProps = (state: ReduxState): ReduxState => ({
    user: state.user,
});

const mapDispatchToProps = (dispatch: (arg0: any) => any): ReduxDispatch => ({
    getUserDetails: (): any => dispatch(getUserDetails({})),
});

const appShellContainerWrapper = connect<ReduxState, ReduxDispatch, OwnProps>(
    mapStateToProps,
    mapDispatchToProps,
);

export default appShellContainerWrapper;
