/* eslint-disable @typescript-eslint/no-explicit-any */
import {
    GET_USER_REQUEST,
    GET_USER,
    GET_USER_FAILURE,
    GET_USER_FINALLY,
} from '../actions/userActions';

import { UserReducerInterface } from './types/userReducer';

export const initialState: UserReducerInterface = {
    status: null,
    error: false,
    isLoading: false,
    details: null,
};

export default function userReducer(state: UserReducerInterface = initialState, action: any): UserReducerInterface {
    switch (action.type) {
    case GET_USER_REQUEST: {
        return {
            ...state,
            isLoading: true,
            details: null,
        };
    }
    case GET_USER: {
        return {
            ...state,
            details: action.transformed,
            status: action.status,
        };
    }
    case GET_USER_FAILURE: {
        return {
            ...state,
            status: action.status,
        };
    }
    case GET_USER_FINALLY: {
        return {
            ...state,
            isLoading: false,
        };
    }
    default: {
        return {
            ...state,
        };
    }
    }
}
