/* eslint-disable @typescript-eslint/no-explicit-any */

// Import actual files
import api from '../../common/utils/apiClient';

// Import type definitions
import { UserApiAction } from './types/userActions';

export const GET_USER = 'GET_USER';
export const GET_USER_REQUEST = `${GET_USER}_REQUEST`;
export const GET_USER_FAILURE = `${GET_USER}_FAILURE`;
export const GET_USER_FINALLY = `${GET_USER}_FINALLY`;


export function getUserDetails(payload: any): UserApiAction {
    return {
        type: GET_USER,
        promise: api.get('/api/v1/auth/user-details', { ...payload }),
        // eslint-disable-next-line @typescript-eslint/explicit-function-return-type
        transformer: (x) => x,
    };
}
